package kr.co.iekorea.mc.adapter;import java.util.ArrayList;import kr.co.iekorea.mc.R;import kr.co.iekorea.mc.xml.MapDTO;import android.app.Activity;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;public class MapAdapter extends BaseAdapter {	public Context ctx;	public Activity activity;	public LayoutInflater inflater;	public ArrayList<MapDTO> items;		public ViewHolder_Map holder;		public MapAdapter(Context ctx, Activity activity, ArrayList<MapDTO> items){		this.ctx = ctx;		this.activity = activity;		this.items = items;		init();	}		public void init(){		this.inflater = (LayoutInflater) ctx.getSystemService(Context.LAYOUT_INFLATER_SERVICE);	}	@Override	public int getCount() {		return items.size();	}	@Override	public Object getItem(int position) {		return items.get(position);	}	@Override	public long getItemId(int position) {		return position;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		if(convertView == null){			convertView = inflater.inflate(R.layout.map_list_item, null);			holder = new ViewHolder_Map();			holder.text = (TextView) convertView.findViewById(R.id.text_map_title);			convertView.setTag(holder);		}else{			holder = (ViewHolder_Map) convertView.getTag();		}				holder.text.setText(items.get(position).TITLE);		return convertView;	}}