package kr.co.iekorea.mc.adapter;import java.util.ArrayList;import kr.co.iekorea.mc.R;import kr.co.iekorea.mc.staticdata.StaticData;import kr.co.iekorea.mc.util.ImageDownloader;import kr.co.iekorea.mc.xml.PresentListDto;import android.app.Activity;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;public class PresentAdapter extends BaseAdapter {	public Activity activity;	public LayoutInflater inflater;	public ArrayList<PresentListDto> items;	public ViewHolder_Present holder;		public PresentAdapter(Activity activity, ArrayList<PresentListDto> items){		this.activity = activity;		this.items = items;		init();	}		public void init(){		inflater = (LayoutInflater) activity.getSystemService(Context.LAYOUT_INFLATER_SERVICE);	}	@Override	public int getCount() {		return items.size();	}	@Override	public Object getItem(int position) {		return items.get(position);	}	@Override	public long getItemId(int position) {		return position;	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		if(convertView == null){			convertView = inflater.inflate(R.layout.present_list_item, null);			holder = new ViewHolder_Present();			holder.image_present = (ImageView) convertView.findViewById(R.id.image_present);			holder.text_company = (TextView) convertView.findViewById(R.id.text_company);			holder.text_present_title = (TextView) convertView.findViewById(R.id.text_present_title);			convertView.setTag(holder);		}else{			holder = (ViewHolder_Present) convertView.getTag();		}				holder.text_present_title.setText(items.get(position).PRESENT_NAME);		holder.text_company.setText(items.get(position).MANUFACTURE_NAME);				try{			ImageDownloader.download(StaticData.DEFAULT_URL+items.get(position).PRESENT_IMAGE, holder.image_present);		}catch(RuntimeException e){}				return convertView;	}}